import React from 'react';
import useTopicContext from '../../useTopicContext';

function ActiveTopic() {
  const {
    deepdiveIntoTopic,
    quizAboutTopic,
    topic,
    briefSummary,
    activeTopic,
  } = useTopicContext();
  return (
    <div className='relative flex w-1/2 h-[800px] items-center bg-tertiary-grey rounded-xl'>
      <img />
      <div>
        <div className='w-full h-[200px] bg-secondary-grey rounded-md'></div>
        <div className='flex flex-col gap-4 px-6 py-8'>
          <p className='text-3xl text-primary-black font-medium'>
            {activeTopic && activeTopic}
          </p>
          <p className='text-xl text-primary-black font-regular'>
            {activeTopic &&
              briefSummary?.topics?.find((item) => item.name === activeTopic)
                ?.summary}
          </p>
        </div>
      </div>

      <div className=' absolute bottom-8 right-6 flex gap-2'>
        <div>
          <button
            onClick={() => deepdiveIntoTopic(activeTopic, topic)}
            className='flex items-center gap-4 px-3 py-2 bg-primary-indigo rounded-md text-sm text-white font-semibold'
          >
            <p>Learn more</p>
            <p>→</p>
          </button>
        </div>
        <div>
          <button
            onClick={() => quizAboutTopic(activeTopic, topic)}
            className='flex items-center gap-4 px-3 py-2 bg-primary-indigo rounded-md text-sm text-white font-semibold'
          >
            <p>Quiz me</p>
            <p>→</p>
          </button>
        </div>
      </div>
    </div>
  );
}

export default ActiveTopic;
