import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import useTopicContext from './useTopicContext';

function TopicQuiz() {
  const navigate = useNavigate();

  const { quizData } = useTopicContext();
  const { activeQuestion, setActiveQuestion } = useState(0);
  return (
    <div className='w-screen h-screen flex justify-center grow bg-white'>
      <div className='w-2/3 flex flex-col items-center gap-8 py-16'>
        <div className='flex gap-4 items-center'>
          <div
            onClick={() => navigate('/')}
            className='flex items-center justify-center px-3 py-2 bg-tertiary-grey rounded-md text-primary-grey cursor-pointer'
          >
            <p className='text-sm'>‚Üê</p>
          </div>

          <p>{quizData?.quiz_title}</p>
        </div>

        {quizData?.questions?.map((item) => {
          return (
            <div class='w-full'>
              <p className='text-lg text-primary-black font-medium'>
                {item?.question}
              </p>
              <div className='flex flex-col gap-2'>
                {item?.options.map((option) => {
                  return (
                    <div className='py-2 px-2 bg-tertiary-grey cursor-pointer'>
                      <p>{option.text}</p>
                    </div>
                  );
                })}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default TopicQuiz;
