import React from 'react';
import useTopicContext from '../../useTopicContext';

function MainSummary() {
  const { briefSummary, activeTopic, setActiveTopic } = useTopicContext();
  return (
    <div className='w-1/2 h-[800px]'>
      <div className='flex flex-col gap-8 w-[400px]'>
        <div className='flex flex-col gap-4 w-[400px]'>
          <p className='text-4xl text-primary-black font-medium'>
            {briefSummary?.title}
          </p>
          <p className='text-xl text-primary-black font-regular'>
            {briefSummary?.summary}
          </p>
        </div>

        <div className='flex flex-col gap-2'>
          {briefSummary?.topics?.slice(0, 4)?.map((item, index) => {
            return (
              <div
                key={index}
                onClick={() => setActiveTopic(item.name)}
                className={`flex justify-between px-3 py-2 rounded-md cursor-pointer ${
                  activeTopic === item?.name
                    ? 'bg-primary-indigo'
                    : 'bg-secondary-indigo'
                }`}
              >
                <p
                  className={`text-sm font-medium ${
                    activeTopic === item?.name
                      ? 'text-white'
                      : 'text-primary-indigo'
                  }`}
                >
                  {item.name}
                </p>
                <p
                  className={`text-sm font-medium ${
                    activeTopic === item?.name
                      ? 'text-secondary-indigo'
                      : 'text-primary-indigo'
                  }`}
                >
                  â†’
                </p>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default MainSummary;
