import React, { useState } from 'react';

function QuizQuestion({ item, activeQuestion, setActiveQuestion }) {
  const [selectedOption, setSelectedOption] = useState('');
  return (
    <div
      key={item.id}
      class='flex flex-col gap-2'
    >
      <p className='text-lg text-primary-black font-medium'>{item?.question}</p>
      <div className='flex flex-col gap-2'>
        {item?.options?.map((option) => {
          return (
            <div
              key={option.id}
              onClick={() => setSelectedOption(option.id)}
              className='px-2 py-2 bg-tertiary-grey rounded-md cursor-pointer'
            >
              <p>ðŸ”˜ {option.text}</p>
            </div>
          );
        })}
      </div>
      <button
        disabled={selectedOption !== item?.correctAnswer}
        onClick={() => setActiveQuestion(activeQuestion + 1)}
        className='py-2 bg-secondary-indigo text-white text-base font-semibold rounded-md'
      >
        Continue
      </button>
      <div></div>
    </div>
  );
}

export default QuizQuestion;
