import React, { useState, useEffect } from 'react';

function App() {
  const [subject, setSubject] = useState('');

  const handleChange = (e) => {
    setSubject(e.target.value);
  };

  return (
    <div className='w-screen h-screen flex flex-col grow items-center bg-white'>
      <div className='w-3/4 h-full flex flex-col gap-4 py-16'>
        <p className='text-lg text-primary-black font-semibold'>
          I want to learn...
        </p>
        <input
          value={subject}
          onChange={handleChange}
          placeholder='Subject'
          className=' text-3xl text-primary-black font-medium py-2 outline-none placeholder:text-primary-grey'
        />
      </div>
    </div>
  );
}

export default App;
