import useTopicContext from '../useTopicContext';

function TopicSearch() {
  const { searchTopic, topic, setTopic } = useTopicContext();

  const handleChange = (e) => {
    setTopic(e.target.value);
  };
  return (
    <div className='flex flex-col gap-4'>
      <p className='text-lg text-primary-black font-regular'>
        I want to learn about...
      </p>
      <div className='flex items-center'>
        <input
          value={topic}
          onChange={handleChange}
          placeholder='What are you curious about?'
          className='w-full text-8xl text-primary-black font-bold py-2 outline-none placeholder:text-primary-grey bg-white'
        />
        <button
          onClick={() => searchTopic()}
          className='w-[48px] h-[48px] rounded-md bg-primary-indigo text-white text-xl font-bold'
        >
          â†’
        </button>
      </div>
    </div>
  );
}

export default TopicSearch;
