import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

import useTopicContext from './useTopicContext';

function TopicDeepdive() {
  const navigate = useNavigate();
  const { deepdiveData, getKeywords } = useTopicContext();

  useEffect(() => {
    getKeywords(deepdiveData?.conclusion);
  }, []);

  return (
    <div className='relative w-screen h-screen flex flex-row justify-center bg-white overflow-x-auto'>
      <div
        onClick={() => navigate('/')}
        className='absolute left-0 top-8 flex items-center justify-center px-3 py-2 bg-tertiary-grey rounded-md text-primary-grey cursor-pointer'
      >
        <p className='text-sm'>‚Üê</p>
      </div>

      <div className='flex-none w-[1200px] bg-tertiary-grey'>
        <p className='text-2xl text-primary-black font-medium'>
          {deepdiveData.topic}
        </p>
        <p>{deepdiveData.overview}</p>
      </div>

      <div className='flex-none flex flex-row gap-8'>
        {deepdiveData?.sections?.map((section, index) => {
          return (
            <div key={index}>
              <div className='flex flex-col gap-2 flex-none w-[1200px]'>
                <p className='text-xl text-primary-indigo font-medium'>
                  {section.title}
                </p>
                <p>{section.description}</p>
              </div>
              <div className='flex-none w-[1200px] flex flex-col gap-1'>
                {section.subsections.map((subsection, subIndex) => {
                  return (
                    <div key={subIndex}>
                      <p className='text-lg text-primary-black font-medium'>
                        {subsection.subTitle}
                      </p>
                      <p>{subsection.content}</p>
                    </div>
                  );
                })}
              </div>
            </div>
          );
        })}
      </div>
      <div className='w-[1200px] flex flex-col flex-none gap-4'>
        <p>{deepdiveData.conclusion}</p>
      </div>
    </div>
  );
}

export default TopicDeepdive;
