import React from 'react';
import { useNavigate } from 'react-router-dom';

import useTopicContext from './useTopicContext';

function TopicQuiz() {
  const navigate = useNavigate();

  const { quizData } = useTopicContext();
  return (
    <div className='w-screen h-screen flex flex-col grow items-center bg-white'>
      <div className='w-2/3 flex flex-col items-center gap-8 py-16'>
        <div className='flex items-center'>
          <div onClick={() => navigate('/')}>
            <p>‚Üê</p>
          </div>

          <p>{quizData?.quiz_title}</p>
        </div>

        <div>
          {quizData?.questions?.map((item) => {
            return (
              <div>
                <p>{item?.question}</p>
                <div>
                  {item?.options.map((option) => {
                    return (
                      <div>
                        <p>{option.text}</p>
                      </div>
                    );
                  })}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default TopicQuiz;
