import React, { useEffect, useRef } from 'react';
import useTopicContext from '../../useTopicContext';
import AskInput from './AskInput';

function Sidesheet() {
  const { showSidesheet, setShowSidesheet } = useTopicContext();
  const sidesheetRef = useRef();

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (sidesheetRef.current && !sidesheetRef.current.contains(e.target)) {
        setShowSidesheet(false);
      }
    };

    window.addEventListener('mousedown', handleClickOutside);
    return () => {
      window.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div
      ref={sidesheetRef}
      className={`fixed top-0 right-0 w-[560px] h-screen flex flex-col bg-white z-20 transform ${
        showSidesheet ? 'translate-x-0' : 'translate-x-full'
      } transition-transform ease-in-out duration-300`}
    >
      <div className='fixed top-0 w-full h-[88px] flex px-6 items-center bg-white'>
        <p className='text-2xl font-bold'>Expert</p>
      </div>
      <div className='flex flex-col-reverse	 grow bg-primary-teal'></div>
      <div className='fixed bottom-0 w-full h-[160px] px-6 pt-4 bg-white'>
        <AskInput />
      </div>
    </div>
  );
}

export default Sidesheet;
