import React, { useState, useEffect, useRef } from 'react';
import CarouselTitleCard from './CarouselTitleCard';
import CarouselCard from './CarouselCard';
import useTopicContext from '../../useTopicContext';

function Carousel({ parentRef }) {
  const { courseData, focusedSlide } = useTopicContext();
  const [carouselPadding, setCarouselPadding] = useState(0);
  const carouselRef = useRef();
  console.log('focusedSlide:', focusedSlide);

  useEffect(() => {
    const handleResize = () => {
      if (parentRef.current) {
        const containerWidth = parentRef.current.offsetWidth;
        const windowWidth = window.innerWidth;
        const padding = (windowWidth - containerWidth) / 2;
        setCarouselPadding(padding > 0 ? padding : 0);
      }
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [parentRef]);

  useEffect(() => {
    if (
      carouselRef.current &&
      courseData?.topics &&
      courseData.topics.length > 0
    ) {
      // Assuming each slide has a fixed width, find one slide's width
      const slideElement = carouselRef.current.querySelector(':scope > div ');

      const slideWidth = slideElement ? slideElement.offsetWidth : 0;

      // Calculate the scroll position needed to center the focused slide
      const scrollPosition =
        slideWidth * focusedSlide + window.innerWidth / 2 + slideWidth;

      // Adjust the scroll position of the carousel
      carouselRef.current.scrollLeft = scrollPosition > 0 ? scrollPosition : 0;
    }
  }, [focusedSlide, courseData]);

  return (
    <div
      ref={carouselRef}
      className='flex flex-row gap-8 w-full min-h-[640px] overflow-x-auto hide-scrollbar'
      style={{
        paddingLeft: `${carouselPadding}px`,
        paddingRight: `${carouselPadding}px`,
      }}
    >
      <CarouselTitleCard />
      <div className='w-max h-full flex flex-row gap-8 '>
        {courseData?.topics &&
          courseData?.topics?.map((subtopic, index) => (
            <CarouselCard
              key={index}
              index={index}
              unit={subtopic.unit}
              introduction={subtopic.introduction}
              content1={subtopic.inDepthContent1}
              content2={subtopic.inDepthContent2}
            />
          ))}
      </div>
    </div>
  );
}

export default Carousel;
