import React from 'react';

function CourseCard({ course }) {
  return (
    <div className='w-full h-max bg-white rounded-2xl shadow-md cursor-pointer overflow-hidden'>
      {course.thumbnail ? (
        <div className='w-full hh-[168px]  overflow-hidden'>
          <img src={''} />
        </div>
      ) : (
        <div className='w-full h-[196px] p-4 overflow-hidden bg-primary-teal'>
          <p className='text-lg text-tertiary-tan font-tertiary'>
            {course.summary}
          </p>
        </div>
      )}

      <div className='border-t border-secondary-tan px-4 py-4'>
        <p className='text-xl font-bold '>{course?.title}</p>
      </div>
    </div>
  );
}

export default CourseCard;
