import React, { useState } from 'react';
import Loader from '../Loader';

function FormInput({ authFunction }) {
  const [isAuthenticating, setIsAuthenticating] = useState(false);
  const [formFields, setFormFields] = useState({
    email: '',
    password: '',
  });

  const handleChange = (event) => {};
  return (
    <div>
      <form onSubmit={authFunction}>
        <div class='grid gap-2 gap-6 bg-secondary-tan rounded-3xl'>
          <div>
            <p class='text-2xl font-bold'>Welcome back</p>
            <p class='font-medium text-primary-grey'>Sign in to your account</p>
          </div>
          <div class='flex flex-col gap-4'>
            <div class='flex flex-col gap-1'>
              <label class='text-primary-black font-medium'>Email</label>
              <input
                type='email'
                name='email'
                value={formFields.email}
                onChange={handleChange}
                placeholder='you@email.com'
                class='w-[328px] text-primary-black bg-tertiary-grey rounded-lg px-4 py-2'
              />
            </div>
            <div class='flex flex-col gap-1'>
              <label class='text-primary-black font-medium'>Password</label>
              <input
                type='password'
                name='password'
                value={formFields.password}
                onChange={handleChange}
                placeholder=''
                class='w-[328px] text-primary-black bg-tertiary-grey rounded-lg px-4 py-2'
              />
            </div>
          </div>
          <button
            type='submit'
            class='flex items-center justify-center w-full bg-primary-blue text-white font-semibold py-2 rounded-lg'
          >
            {isAuthenticating ? (
              <div class='flex items-center gap-2'>
                <p>Sign in</p>
                <Loader />
              </div>
            ) : (
              'Sign in'
            )}
          </button>

          <p class='text-primary-grey'>
            Don't have an account?{' '}
            <a
              class='text-primary-blue cursor-pointer'
              href='/signup'
            >
              Sign up
            </a>
          </p>
        </div>
      </form>
    </div>
  );
}

export default FormInput;
