import React, { useContext, useState } from 'react';
import useTopicContext from '../../useTopicContext';
import Sparkles from '../../assets/sparkes.svg';
import AddSlideQuestion from './AddSlideQuestion';

function AddSlide({ content1, content2 }) {
  const { relevantQuestions, getRelevantQuestions } = useTopicContext();
  const [showQuestions, setShowQuestions] = useState(false);

  const mergedParagraph = `${content1} ${content2}`;

  const handleClick = () => {
    getRelevantQuestions(mergedParagraph);
    setShowQuestions(true);
  };

  console.log(relevantQuestions);

  return (
    <div className='absolute bottom-4 right-4 flex flex-col gap-2 items-end'>
      {showQuestions && (
        <div className='flex flex-col gap-2 items-end'>
          {relevantQuestions &&
            relevantQuestions.map((question, index) => (
              <AddSlideQuestion
                key={index}
                question={question}
              />
            ))}
        </div>
      )}
      <div className='w-[48px] h-[48px] bg-primary-tan rounded-full overflow-hidden'>
        <button
          onClick={handleClick}
          className='w-full h-full flex items-center justify-center'
        >
          <img
            src={Sparkles}
            className='w-[16px] h-[16px]'
          />
        </button>
      </div>
    </div>
  );
}

export default AddSlide;
