    <div
      className={`fixed top-0 right-0 w-[560px] h-screen bg-white z-20 transform ${
        showSidesheet ? 'translate-x-0' : 'translate-x-full'
      } transition-transform ease-in-out duration-300`}
    >
      <div></div>
      <div></div>
      <div></div>
    </div>
  );
}

export default Sidesheet;
