import React, { useEffect, useRef } from 'react';
import useTopicContext from '../../useTopicContext';

function AskInput() {
  const { setShowSidesheet } = useTopicContext();
  const sidesheetRef = useRef();

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (sidesheetRef.current && !sidesheetRef.current.contains(e.target)) {
        setShowSidesheet(false);
      }
    };

    window.addEventListener('mousedown', handleClickOutside);
    return () => {
      window.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div
      ref={sidesheetRef}
      className='relative w-full h-max flex grow'
    >
      <input
        placeholder='Ask a follow up'
        className='w-full h-[48px] px-4 bg-secondary-tan border border-primary-tan rounded-full text-primary-tan placeholder:text-primary-tan outline-none'
      />
      <div className='absolute top-1 right-1 w-[40px] h-[40px] bg-primary-tan rounded-full overflow-hidden'>
        <button
          onClick={() => setShowSidesheet(true)}
          className='w-full h-full flex items-center justify-center text-medium text-tertiary-tan font-semibold'
        >
          â†’
        </button>
      </div>
    </div>
  );
}

export default AskInput;
