import React, { useState, useEffect } from 'react';
import useTopicContext from '../../useTopicContext';

function Search({ topicSearchRef }) {
  const { topic, setTopic, createCourse } = useTopicContext();

  useEffect(() => {
    const handleKeyDown = (event) => {
      if ((event.metaKey || event.ctrlKey) && event.key === 'Enter') {
        console.log('TOPIC KEYDOWN', topic);
        createCourse(topic);
      }
    };

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [topic]);

  const submitSearch = (topic) => {
    createCourse(topic);
  };

  const handleChange = (e) => {
    setTopic(e.target.value);
  };
  return (
    <div
      ref={topicSearchRef}
      className='w-full h-max bg-secondary-tan'
    >
      <input
        value={topic}
        onChange={handleChange}
        placeholder='Learn about anything...'
        className='w-full text-6xl text-primary-tan font-regular py-2 bg-secondary-tan outline-none placeholder:text-primary-tan '
      />
    </div>
  );
}

export default Search;
