import React from 'react';
import useTopicContext from '../../useTopicContext';

function TableofContentsDropdown() {
  const { courseData } = useTopicContext();

  return (
    <div className='absolute mt-[56px] flex flex-col px-4 py-4 bg-white w-[max] shadow-sm z-10 cursor-pointer'>
      {courseData &&
        courseData.topics.map((topic, index) => (
          <div className='w-max'>
            <p>{topic.unit}</p>
          </div>
        ))}
    </div>
  );
}

export default TableofContentsDropdown;
